import React, { useState, useEffect } from 'react';
import { 
  DocumentTextIcon, 
  BanknotesIcon, 
  CubeIcon, 
  ClockIcon,
  FunnelIcon,
  MagnifyingGlassIcon
} from '@heroicons/react/24/outline';
import { getEvents } from '../services/api';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [error, setError] = useState(null);
  const eventsPerPage = 10;

  // Fetch events data
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setIsLoading(true);
        const data = await getEvents({
          type: filter !== 'all' && !['processed', 'pending', 'ignored'].includes(filter) ? filter : undefined,
          status: ['processed', 'pending', 'ignored'].includes(filter) ? filter : undefined
        });
        setEvents(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching events:', error);
        setError('Failed to load events. Please try again later.');
        setIsLoading(false);

        // Fallback to mock data for development
        const mockEvents = [];
        const types = ['nft_mint', 'nft_transfer', 'coin_transfer', 'collection_creation', 'other'];
        const descriptions = [
          'NFT transferred to account',
          'Coins deposited to account',
          'New NFT minted',
          'New NFT collection created',
          'Token swap executed',
          'Staking rewards claimed',
          'Governance vote cast',
          'Liquidity added to pool'
        ];
        const accounts = [
          '0x0108bc32f7de18a5f6e1e7d6ee7aff9f5fc858d0d87ac0da94dd8d2a5d267d6b',
          '0x2c7bccf7b31baf770fdbcc768d9e9cb3d87805e255355df5db32ac9a669010a2',
          '0x1',
          '0x3'
        ];
        const statuses = ['processed', 'pending', 'ignored'];

        // Generate 50 mock events
        for (let i = 1; i <= 50; i++) {
          const date = new Date();
          date.setMinutes(date.getMinutes() - i * 10);

          mockEvents.push({
            id: i,
            type: types[Math.floor(Math.random() * types.length)],
            description: descriptions[Math.floor(Math.random() * descriptions.length)],
            account: accounts[Math.floor(Math.random() * accounts.length)],
            timestamp: date.toISOString(),
            status: statuses[Math.floor(Math.random() * statuses.length)],
            details: {
              transactionHash: `0x${Math.random().toString(16).substr(2, 64)}`,
              blockNumber: Math.floor(Math.random() * 1000000) + 2000000,
              gasUsed: Math.floor(Math.random() * 1000000) + 100000,
              fee: (Math.random() * 0.01).toFixed(8)
            }
          });
        }

        setEvents(mockEvents);
      }
    };

    fetchEvents();

    // Set up polling for real-time updates
    const interval = setInterval(() => {
      fetchEvents();
    }, 30000); // Poll every 30 seconds

    return () => clearInterval(interval);
  }, [filter]);

  // Helper function to get status color
  const getStatusColor = (status) => {
    switch (status) {
      case 'processed':
        return 'bg-green-100 text-green-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'ignored':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Helper function to get event type icon
  const getEventTypeIcon = (type) => {
    switch (type) {
      case 'nft_mint':
      case 'nft_transfer':
        return <DocumentTextIcon className="h-5 w-5 text-blue-500" />;
      case 'coin_transfer':
        return <BanknotesIcon className="h-5 w-5 text-yellow-500" />;
      case 'collection_creation':
        return <CubeIcon className="h-5 w-5 text-purple-500" />;
      default:
        return <ClockIcon className="h-5 w-5 text-gray-500" />;
    }
  };

  // Filter events based on current filter and search query
  const filteredEvents = events.filter(event => {
    const matchesFilter = filter === 'all' || event.type === filter || event.status === filter;
    const matchesSearch = searchQuery === '' || 
      event.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
      event.account.toLowerCase().includes(searchQuery.toLowerCase()) ||
      event.details.transactionHash.toLowerCase().includes(searchQuery.toLowerCase());

    return matchesFilter && matchesSearch;
  });

  // Pagination
  const indexOfLastEvent = currentPage * eventsPerPage;
  const indexOfFirstEvent = indexOfLastEvent - eventsPerPage;
  const currentEvents = filteredEvents.slice(indexOfFirstEvent, indexOfLastEvent);
  const totalPages = Math.ceil(filteredEvents.length / eventsPerPage);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center h-96">
        <div className="text-lg text-gray-500">{error}</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filters and search */}
      <div className="bg-white shadow rounded-lg p-4">
        <div className="flex flex-col sm:flex-row justify-between items-center space-y-4 sm:space-y-0">
          <div className="flex space-x-2">
            <button
              onClick={() => setFilter('all')}
              className={`px-3 py-2 rounded-md text-sm font-medium ${
                filter === 'all' 
                  ? 'bg-primary-100 text-primary-700' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              All Events
            </button>
            <button
              onClick={() => setFilter('nft_mint')}
              className={`px-3 py-2 rounded-md text-sm font-medium ${
                filter === 'nft_mint' 
                  ? 'bg-blue-100 text-blue-700' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              NFT Mints
            </button>
            <button
              onClick={() => setFilter('nft_transfer')}
              className={`px-3 py-2 rounded-md text-sm font-medium ${
                filter === 'nft_transfer' 
                  ? 'bg-blue-100 text-blue-700' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              NFT Transfers
            </button>
            <button
              onClick={() => setFilter('coin_transfer')}
              className={`px-3 py-2 rounded-md text-sm font-medium ${
                filter === 'coin_transfer' 
                  ? 'bg-yellow-100 text-yellow-700' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              Coin Transfers
            </button>
          </div>

          <div className="relative w-full sm:w-64">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
            </div>
            <input
              type="text"
              className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
              placeholder="Search events..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>

        <div className="mt-4 flex justify-between items-center">
          <div className="text-sm text-gray-500">
            Showing {filteredEvents.length > 0 ? indexOfFirstEvent + 1 : 0} to {Math.min(indexOfLastEvent, filteredEvents.length)} of {filteredEvents.length} events
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-500">Status:</span>
            <button
              onClick={() => setFilter('all')}
              className={`px-2 py-1 rounded-md text-xs font-medium ${
                filter === 'all' 
                  ? 'bg-gray-200 text-gray-800' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              All
            </button>
            <button
              onClick={() => setFilter('processed')}
              className={`px-2 py-1 rounded-md text-xs font-medium ${
                filter === 'processed' 
                  ? 'bg-green-100 text-green-800' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              Processed
            </button>
            <button
              onClick={() => setFilter('pending')}
              className={`px-2 py-1 rounded-md text-xs font-medium ${
                filter === 'pending' 
                  ? 'bg-yellow-100 text-yellow-800' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              Pending
            </button>
            <button
              onClick={() => setFilter('ignored')}
              className={`px-2 py-1 rounded-md text-xs font-medium ${
                filter === 'ignored' 
                  ? 'bg-gray-100 text-gray-800' 
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              Ignored
            </button>
          </div>
        </div>
      </div>

      {/* Events table */}
      <div className="bg-white shadow overflow-hidden sm:rounded-lg">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Event
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Account
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Timestamp
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Transaction
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {currentEvents.map((event) => (
                <tr key={event.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="flex-shrink-0">
                        {getEventTypeIcon(event.type)}
                      </div>
                      <div className="ml-4">
                        <div className="text-sm font-medium text-gray-900">{event.description}</div>
                        <div className="text-xs text-gray-500">{event.type}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{event.account.substring(0, 8)}...{event.account.substring(event.account.length - 6)}</div>
                    <div className="text-xs text-gray-500">Aptos Account</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{new Date(event.timestamp).toLocaleTimeString()}</div>
                    <div className="text-xs text-gray-500">{new Date(event.timestamp).toLocaleDateString()}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{event.details.transactionHash.substring(0, 10)}...</div>
                    <div className="text-xs text-gray-500">Block: {event.details.blockNumber}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(event.status)}`}>
                      {event.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button className="text-primary-600 hover:text-primary-900 mr-3">View</button>
                    <button className="text-primary-600 hover:text-primary-900">Process</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
            <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
              <div>
                <p className="text-sm text-gray-700">
                  Showing <span className="font-medium">{indexOfFirstEvent + 1}</span> to <span className="font-medium">{Math.min(indexOfLastEvent, filteredEvents.length)}</span> of{' '}
                  <span className="font-medium">{filteredEvents.length}</span> results
                </p>
              </div>
              <div>
                <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
                  <button
                    onClick={() => paginate(Math.max(1, currentPage - 1))}
                    disabled={currentPage === 1}
                    className={`relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium ${
                      currentPage === 1 ? 'text-gray-300' : 'text-gray-500 hover:bg-gray-50'
                    }`}
                  >
                    <span className="sr-only">Previous</span>
                    &larr;
                  </button>

                  {[...Array(totalPages).keys()].map(number => (
                    <button
                      key={number + 1}
                      onClick={() => paginate(number + 1)}
                      className={`relative inline-flex items-center px-4 py-2 border border-gray-300 bg-white text-sm font-medium ${
                        currentPage === number + 1
                          ? 'z-10 bg-primary-50 border-primary-500 text-primary-600'
                          : 'text-gray-500 hover:bg-gray-50'
                      }`}
                    >
                      {number + 1}
                    </button>
                  ))}

                  <button
                    onClick={() => paginate(Math.min(totalPages, currentPage + 1))}
                    disabled={currentPage === totalPages}
                    className={`relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium ${
                      currentPage === totalPages ? 'text-gray-300' : 'text-gray-500 hover:bg-gray-50'
                    }`}
                  >
                    <span className="sr-only">Next</span>
                    &rarr;
                  </button>
                </nav>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EventsPage;
