import React, { useState, useEffect } from 'react';
import { 
  CheckIcon,
  XMarkIcon,
  ArrowPathIcon 
} from '@heroicons/react/24/outline';
import { getSettings, updateSettings } from '../services/api';

const SettingsPage = () => {
  const [settings, setSettings] = useState({
    pollingInterval: 60,
    discordNotifications: true,
    monitoredAccounts: [],
    eventTypes: {
      nft_mint: true,
      nft_transfer: true,
      coin_transfer: true,
      collection_creation: true,
      other: false
    },
    apiEndpoint: 'https://fullnode.mainnet.aptoslabs.com/v1',
    discordWebhook: 'https://discord.com/api/webhooks/123456789/abcdefg',
    aiModel: 'gpt-4',
    autoProcessEvents: true
  });

  const [newAccount, setNewAccount] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [saveSuccess, setSaveSuccess] = useState(false);
  const [error, setError] = useState(null);

  // Fetch settings from API
  useEffect(() => {
    const fetchSettings = async () => {
      try {
        setIsLoading(true);
        const data = await getSettings();
        setSettings(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching settings:', error);
        setError('Failed to load settings. Please try again later.');
        setIsLoading(false);

        // Fallback to default settings for development
        setSettings({
          pollingInterval: 60,
          discordNotifications: true,
          monitoredAccounts: [
            '0x0108bc32f7de18a5f6e1e7d6ee7aff9f5fc858d0d87ac0da94dd8d2a5d267d6b',
            '0x2c7bccf7b31baf770fdbcc768d9e9cb3d87805e255355df5db32ac9a669010a2',
            '0x1'
          ],
          eventTypes: {
            nft_mint: true,
            nft_transfer: true,
            coin_transfer: true,
            collection_creation: true,
            other: false
          },
          apiEndpoint: 'https://fullnode.mainnet.aptoslabs.com/v1',
          discordWebhook: 'https://discord.com/api/webhooks/123456789/abcdefg',
          aiModel: 'gpt-4',
          autoProcessEvents: true
        });
      }
    };

    fetchSettings();
  }, []);

  // Save settings
  const handleSaveSettings = async () => {
    try {
      setIsSaving(true);
      await updateSettings(settings);
      setSaveSuccess(true);
      setIsSaving(false);

      // Reset success message after 3 seconds
      setTimeout(() => {
        setSaveSuccess(false);
      }, 3000);
    } catch (error) {
      console.error('Error saving settings:', error);
      setError('Failed to save settings. Please try again later.');
      setIsSaving(false);
    }
  };

  // Add new account to monitored accounts
  const handleAddAccount = () => {
    if (newAccount && !settings.monitoredAccounts.includes(newAccount)) {
      setSettings({
        ...settings,
        monitoredAccounts: [...settings.monitoredAccounts, newAccount]
      });
      setNewAccount('');
    }
  };

  // Remove account from monitored accounts
  const handleRemoveAccount = (account) => {
    setSettings({
      ...settings,
      monitoredAccounts: settings.monitoredAccounts.filter(acc => acc !== account)
    });
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name.includes('.')) {
      // Handle nested properties (e.g., eventTypes.nft_mint)
      const [parent, child] = name.split('.');
      setSettings({
        ...settings,
        [parent]: {
          ...settings[parent],
          [child]: type === 'checkbox' ? checked : value
        }
      });
    } else {
      // Handle top-level properties
      setSettings({
        ...settings,
        [name]: type === 'checkbox' ? checked : value
      });
    }
  };

  // Handle toggle event type
  const handleToggleEventType = (type) => {
    setSettings({
      ...settings,
      eventTypes: {
        ...settings.eventTypes,
        [type]: !settings.eventTypes[type]
      }
    });
  };

  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="px-4 py-5 sm:px-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">Agent Settings</h3>
          <p className="mt-1 max-w-2xl text-sm text-gray-500">
            Configure how the blockchain monitoring agent operates.
          </p>
        </div>
        <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
          <div className="grid grid-cols-1 gap-y-6 gap-x-4 sm:grid-cols-6">
            <div className="sm:col-span-3">
              <label htmlFor="polling-interval" className="block text-sm font-medium text-gray-700">
                Polling Interval (seconds)
              </label>
              <div className="mt-1">
                <input
                  type="number"
                  name="pollingInterval"
                  id="polling-interval"
                  min="10"
                  className="shadow-sm focus:ring-primary-500 focus:border-primary-500 block w-full sm:text-sm border-gray-300 rounded-md"
                  value={settings.pollingInterval}
                  onChange={handleInputChange}
                />
              </div>
              <p className="mt-2 text-sm text-gray-500">
                How frequently the agent checks for new blockchain events.
              </p>
            </div>

            <div className="sm:col-span-3">
              <label htmlFor="api-endpoint" className="block text-sm font-medium text-gray-700">
                Aptos API Endpoint
              </label>
              <div className="mt-1">
                <input
                  type="text"
                  name="apiEndpoint"
                  id="api-endpoint"
                  className="shadow-sm focus:ring-primary-500 focus:border-primary-500 block w-full sm:text-sm border-gray-300 rounded-md"
                  value={settings.apiEndpoint}
                  onChange={handleInputChange}
                />
              </div>
              <p className="mt-2 text-sm text-gray-500">
                The Aptos blockchain node URL to connect to.
              </p>
            </div>

            <div className="sm:col-span-3">
              <label htmlFor="discord-webhook" className="block text-sm font-medium text-gray-700">
                Discord Webhook URL
              </label>
              <div className="mt-1">
                <input
                  type="text"
                  name="discordWebhook"
                  id="discord-webhook"
                  className="shadow-sm focus:ring-primary-500 focus:border-primary-500 block w-full sm:text-sm border-gray-300 rounded-md"
                  value={settings.discordWebhook}
                  onChange={handleInputChange}
                />
              </div>
              <p className="mt-2 text-sm text-gray-500">
                Discord webhook for notifications.
              </p>
            </div>

            <div className="sm:col-span-3">
              <label htmlFor="ai-model" className="block text-sm font-medium text-gray-700">
                AI Model
              </label>
              <div className="mt-1">
                <select
                  id="ai-model"
                  name="aiModel"
                  className="shadow-sm focus:ring-primary-500 focus:border-primary-500 block w-full sm:text-sm border-gray-300 rounded-md"
                  value={settings.aiModel}
                  onChange={handleInputChange}
                >
                  <option value="gpt-4">GPT-4</option>
                  <option value="gpt-3.5-turbo">GPT-3.5 Turbo</option>
                  <option value="claude-3">Claude 3</option>
                </select>
              </div>
              <p className="mt-2 text-sm text-gray-500">
                AI model used for analyzing blockchain events.
              </p>
            </div>

            <div className="sm:col-span-6">
              <div className="flex items-start">
                <div className="flex items-center h-5">
                  <input
                    id="discord-notifications"
                    name="discordNotifications"
                    type="checkbox"
                    className="focus:ring-primary-500 h-4 w-4 text-primary-600 border-gray-300 rounded"
                    checked={settings.discordNotifications}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="ml-3 text-sm">
                  <label htmlFor="discord-notifications" className="font-medium text-gray-700">
                    Enable Discord Notifications
                  </label>
                  <p className="text-gray-500">Send notifications to Discord when significant events are detected.</p>
                </div>
              </div>
            </div>

            <div className="sm:col-span-6">
              <div className="flex items-start">
                <div className="flex items-center h-5">
                  <input
                    id="auto-process"
                    name="autoProcessEvents"
                    type="checkbox"
                    className="focus:ring-primary-500 h-4 w-4 text-primary-600 border-gray-300 rounded"
                    checked={settings.autoProcessEvents}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="ml-3 text-sm">
                  <label htmlFor="auto-process" className="font-medium text-gray-700">
                    Auto-Process Events
                  </label>
                  <p className="text-gray-500">Automatically process significant events without manual approval.</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="px-4 py-5 sm:px-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">Monitored Accounts</h3>
          <p className="mt-1 max-w-2xl text-sm text-gray-500">
            Aptos accounts to monitor for events.
          </p>
        </div>
        <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
          <div className="mb-4">
            <div className="flex rounded-md shadow-sm">
              <input
                type="text"
                name="new-account"
                id="new-account"
                className="focus:ring-primary-500 focus:border-primary-500 flex-1 block w-full rounded-none rounded-l-md sm:text-sm border-gray-300"
                placeholder="0x..."
                value={newAccount}
                onChange={(e) => setNewAccount(e.target.value)}
              />
              <button
                type="button"
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-r-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                onClick={handleAddAccount}
              >
                Add Account
              </button>
            </div>
          </div>

          <div className="mt-4">
            <ul className="divide-y divide-gray-200">
              {settings.monitoredAccounts.map((account) => (
                <li key={account} className="py-3 flex justify-between items-center">
                  <div className="flex items-center">
                    <span className="text-sm font-medium text-gray-900">{account}</span>
                  </div>
                  <button
                    type="button"
                    className="ml-2 inline-flex items-center p-1 border border-transparent rounded-full shadow-sm text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                    onClick={() => handleRemoveAccount(account)}
                  >
                    <XMarkIcon className="h-4 w-4" aria-hidden="true" />
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="px-4 py-5 sm:px-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">Event Types</h3>
          <p className="mt-1 max-w-2xl text-sm text-gray-500">
            Configure which types of blockchain events to monitor.
          </p>
        </div>
        <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
          <div className="space-y-4">
            {Object.entries(settings.eventTypes).map(([type, enabled]) => (
              <div key={type} className="flex items-start">
                <div className="flex items-center h-5">
                  <input
                    id={`event-type-${type}`}
                    name={`eventTypes.${type}`}
                    type="checkbox"
                    className="focus:ring-primary-500 h-4 w-4 text-primary-600 border-gray-300 rounded"
                    checked={enabled}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="ml-3 text-sm">
                  <label htmlFor={`event-type-${type}`} className="font-medium text-gray-700">
                    {type.split('_').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}
                  </label>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="flex justify-end">
        <button
          type="button"
          className="inline-flex items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 mr-3"
        >
          Reset to Defaults
        </button>
        <button
          type="button"
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          onClick={handleSaveSettings}
          disabled={isSaving}
        >
          {isSaving ? (
            <>
              <ArrowPathIcon className="animate-spin -ml-1 mr-2 h-4 w-4" />
              Saving...
            </>
          ) : (
            'Save Settings'
          )}
        </button>
      </div>

      {saveSuccess && (
        <div className="rounded-md bg-green-50 p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <CheckIcon className="h-5 w-5 text-green-400" aria-hidden="true" />
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium text-green-800">
                Settings saved successfully!
              </p>
            </div>
          </div>
        </div>
      )}

      {error && (
        <div className="rounded-md bg-red-50 p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <XMarkIcon className="h-5 w-5 text-red-400" aria-hidden="true" />
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium text-red-800">
                {error}
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SettingsPage;
